import React, { Component } from "react";
import { connect } from "react-redux";
import { Field, reduxForm, Form } from "redux-form";
import Button from "@material-ui/core/Button";
import TextField from "@material-ui/core/TextField";
import Visibility from "@material-ui/icons/Visibility";
import VisibilityOff from "@material-ui/icons/VisibilityOff";
import InputAdornment from "@material-ui/core/InputAdornment";
import IconButton from "@material-ui/core/IconButton";
import { Link } from "react-router-dom";

const handleClickShowPassword = () => {
  this.setState(state => ({ showPassword: !state.showPassword }));
};

const customField = ({ label }) => (
  <TextField color="primary" helperText={label} label={label} />
);

const customPassField = ({ label, showPassword = false }) => (
  <TextField
    color="primary"
    helperText={label}
    label={label}
    type="password"
    InputProps={{
      endAdornment: (
        <InputAdornment position="end">
          <IconButton
            aria-label="Toggle password visibility"
            onClick={handleClickShowPassword}
          >
            {this.state.showPassword ? <VisibilityOff /> : <Visibility />}
          </IconButton>
        </InputAdornment>
      )
    }}
  />
);

class SigninPage extends Component {
  constructor(props) {
    super(props);
    this.state = {
      showPassword: false
    };
  }
  render() {
    const { handleSubmit, pristine, reset, submitting, errors } = this.props;
    return (
      <Form onSubmit={handleSubmit}>
        <div style={{ textAlign: "center" }}>
          <div>
            <Field label="Your name" component={customField} name="name" />
          </div>
          <div>
            <Field component={customField} name="email" label="email" />
          </div>
          <div>
            <Field
              component={customPassField}
              name="password"
              label="password"
              type="password"
            />
          </div>
          <div>
            <Field
              component={customPassField}
              name="passwordConf"
              label="password"
              type="password"
            />
          </div>
          {errors.name && (
            <div className="alert alert-danger">{errors.name}</div>
          )}
          {errors.email && (
            <div className="alert alert-danger">{errors.email}</div>
          )}
          {errors.password && (
            <div className="alert alert-danger">{errors.password}</div>
          )}
          {errors.passwordconf && (
            <div className="alert alert-danger">{errors.passwordconf}</div>
          )}
          <div>
            <Button
              color="primary"
              variant="contained"
              type="submit"
              disabled={pristine || submitting}
            >
              Sign in
            </Button>
            <Button
              color="primary"
              variant="contained"
              type="button"
              disabled={pristine}
              onClick={reset}
            >
              Clear Values
            </Button>
            <Link to="/login">
              <Button color="secondary" variant="contained" type="button">
                I have an account
              </Button>
            </Link>
          </div>
        </div>
      </Form>
    );
  }
}

const mapStateToProps = state => ({
  errors: state.errors
});

SigninPage = connect(mapStateToProps)(SigninPage);

export default reduxForm({
  form: "signin"
})(SigninPage);
