import React from "react";
import { connect } from "react-redux";
import { Field, reduxForm, Form } from "redux-form";
import Button from "@material-ui/core/Button";
import TextField from "@material-ui/core/TextField";
import { Link } from "react-router-dom";

const customField = ({ input, label, meta: { touched, error }, ...custom }) => (
  <TextField color="primary" helperText={label} label={label} />
);

let SigninPage = props => {
  const { handleSubmit, pristine, reset, submitting, errors } = props;
  return (
    <Form onSubmit={handleSubmit}>
      <div style={{ textAlign: "center" }}>
        <div>
          <Field label="Your name" component={customField} name="name" />
        </div>
        <div>
          <label>Your Email</label>
          <Field
            placeholder="your email"
            component="input"
            name="email"
            type="email"
          />
        </div>
        <div>
          <label>Choose a Password</label>
          <Field
            placeholder="your password"
            component="input"
            name="password"
            type="password"
          />
        </div>
        <div>
          <label>Repeat your Password</label>

          <Field
            placeholder="your password"
            component="input"
            name="passwordconf"
            type="password"
          />
        </div>
        {errors.name && <div className="alert alert-danger">{errors.name}</div>}
        {errors.email && (
          <div className="alert alert-danger">{errors.email}</div>
        )}
        {errors.password && (
          <div className="alert alert-danger">{errors.password}</div>
        )}
        {errors.passwordconf && (
          <div className="alert alert-danger">{errors.passwordconf}</div>
        )}
        <div>
          <Button
            color="primary"
            variant="contained"
            type="submit"
            disabled={pristine || submitting}
          >
            Sign in
          </Button>
          <Button
            color="primary"
            variant="contained"
            type="button"
            disabled={pristine}
            onClick={reset}
          >
            Clear Values
          </Button>
          <Link to="/login">
            <Button color="secondary" variant="contained" type="button">
              I have an account
            </Button>
          </Link>
        </div>
      </div>
    </Form>
  );
};

const mapStateToProps = state => ({
  errors: state.errors
});

SigninPage = connect(mapStateToProps)(SigninPage);

export default reduxForm({
  form: "signin"
})(SigninPage);
