import React, { Component } from "react";
import PropTypes from "prop-types";
import { connect } from "react-redux";
import { fetchBookDetails } from "../actions/booksActions";
import { Button } from "reactstrap";

class FetchBooksDetails extends Component {
  render() {
    const { id } = this.props.match.params;
    console.log("f", this.props.books.map(t => t.title));
    return (
      <div style={{ textAlign: "center" }}>
        {this.props.books.map((t, index) => (
          <div key={index}>
            <h1>{t.title}</h1>
            <h3>{t.subtitle}</h3>
            <a href={t.url}>
              <img src={t.image} alt="" />
            </a>
            <h3>{t.price}</h3>
          </div>
        ))}
        <Button>Click to buy</Button>
      </div>
    );
  }
}

const mapStateToProps = state => ({
  books: state.books.books
});

FetchBooksDetails.propTypes = {
  books: PropTypes.array.isRequired
};

export default connect(
  mapStateToProps,
  { fetchBookDetails }
)(FetchBooksDetails);
