import { FETCH_BOOKS, SEARCH_BOOK } from "../actions/types";

export default function(state = {books : []}, action) {
  switch (action.type) {
    case FETCH_BOOKS:
      return {
          ...state, books: action.books
      };
case SEARCH_BOOK:
return {
