import { FETCH_BOOKS } from "../actions/types";

const initialState = {
  books
}

export default function(state = initialState, action) {
  switch (action.type) {
    case FETCH_BOOKS:
      return {
          ...state, books: action.books
      };

  default :
  return state
  }
}
